import React, { useState } from "react";

function App() {
  const [graphUrl, setGraphUrl] = useState(null);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://127.0.0.1:5000/generate_graph");

      if (response.ok) {
        const imageBlob = await response.blob();
        const imageObjectURL = URL.createObjectURL(imageBlob);
        setGraphUrl(imageObjectURL);
      } else {
        alert("Error generating graph");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error during submission");
    }
  };

  return (
    <div className="App">
      <h1>Graph Visualization</h1>
      <button onClick={handleSubmit}>Generate Graph</button>

      {/* Display Graph Image after generation */}
      {graphUrl && (
        <div id="graph-container" style={{ marginTop: "30px" }}>
          <h2>Generated Graph</h2>
          <img src={graphUrl} alt="Generated Graph" className="graph-image" />
          <button
            onClick={() => setGraphUrl(null)}
            id="close-button"
            className="close-button"
          >
            âœ– Close
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
