import React, { useState } from "react";
import { Panzoom } from "@panzoom/panzoom";  // Import Panzoom for interactive zoom
import './App.css';  // Import custom styles for page layout

function App() {
  const [file, setFile] = useState(null);
  const [graphUrl, setGraphUrl] = useState(null);

  // Handle file selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  // Handle form submission and API call to Flask
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      alert("Please upload a file");
      return;
    }

    const formData = new FormData();
    formData.append("code_file", file);

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        const imageBlob = await response.blob();
        const imageObjectURL = URL.createObjectURL(imageBlob);
        setGraphUrl(imageObjectURL);
      } else {
        alert("Error uploading file");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error during file upload");
    }
  };

  return (
    <div className="App">
      <h1>Upload Code File for Visualization</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="file"
          accept=".py,.txt,.md"
          onChange={handleFileChange}
        />
        <button type="submit">Generate Graph</button>
      </form>

      {/* Display generated graph image if available */}
      {graphUrl && (
        <div id="graph-container">
          <h2>Generated Graph</h2>
          <div id="panzoom-container" className="panzoom-container">
            <img
              id="graph_image"
              src={graphUrl}
              alt="Graph Visualization"
              className="graph-image"
            />
          </div>
          {/* Close Button */}
          <button
            onClick={() => setGraphUrl(null)}
            id="close-button"
            className="close-button"
          >
            âœ–
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
