import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [repoOwner, setRepoOwner] = useState("");
  const [repoName, setRepoName] = useState("");
  const [graphUrl, setGraphUrl] = useState(null);
  const [uploadOption, setUploadOption] = useState("file"); // Default to file upload

  // Handle file change
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (uploadOption === "file" && !file) {
      alert("Please upload a file.");
      return;
    }

    if (uploadOption === "repo" && (!repoOwner || !repoName)) {
      alert("Please enter both repository owner and name.");
      return;
    }

    const formData = new FormData();
    if (uploadOption === "file") {
      formData.append("code_file", file);
    } else {
      formData.append("repo_owner", repoOwner);
      formData.append("repo_name", repoName);
    }

    // Debugging step: Log the formData to verify the data being sent
    console.log("Form Data being sent:", formData);

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        const imageBlob = await response.blob();
        const imageObjectURL = URL.createObjectURL(imageBlob);
        setGraphUrl(imageObjectURL);
      } else {
        alert("Error uploading file or fetching repo");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error during submission");
    }
  };

  return (
    <div className="App">
      <h1>Upload Code File or Enter Repository Details</h1>

      <form onSubmit={handleSubmit}>
        <label htmlFor="uploadOption">Select an option:</label>
        <select
          id="uploadOption"
          value={uploadOption}
          onChange={(e) => setUploadOption(e.target.value)}
        >
          <option value="file">Upload File</option>
          <option value="repo">Enter Repo Details</option>
        </select>

        {/* File Upload Section */}
        {uploadOption === "file" && (
          <div id="fileUpload" style={{ marginTop: "20px" }}>
            <label htmlFor="code_file">Upload Code File:</label>
            <input
              type="file"
              accept=".py,.txt,.md"
              onChange={handleFileChange}
            />
          </div>
        )}

        {/* Repository Details Section */}
        {uploadOption === "repo" && (
          <div id="repoDetails" style={{ marginTop: "20px" }}>
            <label htmlFor="repo_owner">Repository Owner:</label>
            <input
              type="text"
              id="repo_owner"
              placeholder="Enter Repo Owner"
              value={repoOwner}
              onChange={(e) => setRepoOwner(e.target.value)}
            />
            <br />
            <label htmlFor="repo_name">Repository Name:</label>
            <input
              type="text"
              id="repo_name"
              placeholder="Enter Repo Name"
              value={repoName}
              onChange={(e) => setRepoName(e.target.value)}
            />
          </div>
        )}

        <button type="submit" style={{ marginTop: "20px" }}>
          Submit
        </button>
      </form>

      {/* Display Graph Image after generation */}
      {graphUrl && (
        <div id="graph-container" style={{ marginTop: "30px" }}>
          <h2>Generated Graph</h2>
          <img src={graphUrl} alt="Generated Graph" className="graph-image" />
          <button
            onClick={() => setGraphUrl(null)}
            id="close-button"
            className="close-button"
          >
            ✖ Close
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
